//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.6;

import "./SmartWallet.sol" ;
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";

contract ECDSAWallet is SmartWallet {
    using ECDSAUpgradeable for bytes32;

    //explicit sizes of nonce, to fit a single storage cell with "owner"
    address _owner;
    uint96 _nonce;

    function __ECDSAWallet_init(address owner) public initializer {
        __SmartWallet_init_unchained();
        __ECDSAWallet_init_unchained(owner);
    }

    function __ECDSAWallet_init_unchained(address owner) internal onlyInitializing {
        _owner = owner;
    }

    function nonce() public view virtual override returns (uint256) {
        return _nonce;
    }

    function _incrementNonce() internal override {
        _nonce++;
    }

    function _verify(bytes32 _sigHash, bytes memory _signature) internal view override {
        require(_owner == _sigHash.toEthSignedMessageHash().recover(_signature), "ECDSAWallet: failed to verify signature");
    }
}

contracts/SmartWallet.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.6;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "./IWallet.sol";
import "hardhat/console.sol";

abstract contract SmartWallet is UUPSUpgradeable, IWallet {
    event LogReceivedEther(address indexed _from, uint256 _amount);
    event LogCall(address indexed _contract, uint256 _value, bytes _data);

    address newImplementation;

    receive() payable external {
        emit LogReceivedEther(msg.sender, msg.value);
    }

    function __SmartWallet_init() public initializer {
        __SmartWallet_init_unchained();
    }

    function __SmartWallet_init_unchained() internal onlyInitializing {
        if (address(this).balance > 0) {
            emit LogReceivedEther(msg.sender, address(this).balance);
        }
    }

    function _verify(bytes32 _sigHash, bytes memory _signature) virtual view internal;
    function _incrementNonce() virtual internal;
    function nonce() virtual public view returns(uint256);

    function prepareUpgrade(address _newImplementation,  bytes calldata _signature) external {
        _verify(keccak256(abi.encode(_newImplementation, nonce())), _signature);
        _incrementNonce();
        newImplementation = _newImplementation;
    }

    function exec(address _contract, uint256 _amount, bytes calldata _data, bytes calldata _signature) external {
        _verify(keccak256(abi.encode(_contract, _amount, nonce(), _data)), _signature);
        _incrementNonce();
        _call(_contract, _amount, _data);
    }

    function batchExec(address[] calldata _contracts, uint256[] calldata _amounts, bytes[] calldata _data, bytes calldata _signature) external {
        _verify(keccak256(abi.encode(_contracts, _amounts, nonce(), _data)), _signature);
        console.log("finished verification");
        _incrementNonce();
        console.log("incremented nonce");

        for (uint32 i = 0; i < _contracts.length; i++) {
            console.log("calling contract", i);
            _call(_contracts[i], _amounts[i], _data[i]);
            console.log("called contract", i);
        }
    }

    function _call(address _contract, uint256 _value, bytes calldata _data) internal {
        (bool ok, bytes memory resp) = _contract.call{ value: _value }(_data);
        emit LogCall(_contract, _value, _data);
        console.log("response", ok, string(resp));
        if (!ok) {
            console.log("Error Message", string(resp));

            assembly {
                revert(add(resp, 32), mload(resp))
            }
        }
    }

    function _authorizeUpgrade(address _newImplementation) internal override {
        require(_newImplementation == newImplementation);
        newImplementation = address(0);
    }

    uint256[49] private __gap;
}

